import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Navigation from './components/Navigation'
import TaskForm from './components/TaskForm'

import { tasks } from './tasks.json'

class App extends Component {
  constructor() {
    super();
    this.state = {
      tasks
    }
    this.handleAddTask = this.handleAddTask.bind(this);
  }

  removeTask(index) {
    this.setState({
      tasks: this.state.tasks.filter((e, i) => {
        return i !== index
      })
    });
  }

  handleAddTask(task) {
    this.setState({
      tasks: [...this.state.tasks, task]
    })
  }

  render(){
    const task = this.state.tasks.map((task, i) => {
      return (
        <div key={i} className="col-md-4">
          <div className="card mt-4">
            <div className="card-header">
              <h3>{ task.tittle }</h3>
              <span className="badge badge-pill badge-danger ml-2">
                { task.priority }
              </span>
            </div>
            <div className="card-body">
              <p>{ task.description }</p>
              <p><mark>{ task.assigned }</mark></p>
            </div>
            <div className="card-footer">
              <button
                className="btn btn-danger"
                onClick={this.removeTask.bind(this, i)}>
                Delete
              </button>
            </div>
          </div>
        </div>
      )
    })

    return (
      <div className="App">
        <Navigation tittle='Tasks' num_task={ this.state.tasks.length }/>

        <div className="container">
          <div className="row mt-4">

            <div className="col-md-4 text-center">
              <TaskForm onAddTask={ this.handleAddTask }></TaskForm>
            </div>

            <div className="col-md-4">
              <div className="row">
                { task }
              </div>
            </div>
          </div>
        </div>


        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
        </header>
      </div>
    );
  }
}

export default App;
